// produce dictionary 

function produceDictionary (japanese, english) {
    let myDictionary = [];
    japanese.forEach((item,index) => {
        let obj = { japanese : item , english : english[index] };
        myDictionary.push(obj)
     });
     return myDictionary;
}

const myDictionary = produceDictionary (arrayJapaneseWords, arrayEnglishWords);

console.log(myDictionary)

// search word in dictionary array

function searchWord (dictionary, word) {
    let filteredArray1 = dictionary.filter(item => item.japanese === word || item.english === word);
    let filteredArray2 = dictionary.filter(item => item.japanese.includes(word) || item.english.includes(word));
    let filteredArray;
    if(filteredArray1.length > 0) {
        filteredArray = [...new Set(filteredArray1)];
    } else {
        filteredArray = [...new Set(filteredArray2)];
    }


    //let filteredArray = [... new Set([...filteredArray1, ...filteredArray2])]
    console.log(filteredArray)
    return filteredArray;
}






